import React, { useEffect, useState } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Badge } from '../components/ui/badge';
import { Button } from '../components/ui/button';
import {
  Activity,
  Cpu,
  HardDrive,
  Zap,
  Play,
  Square,
  RotateCcw
} from 'lucide-react';
import { EngineStatus, SystemMetrics } from '../types';

const Dashboard: React.FC = () => {
  const [engineStatus, setEngineStatus] = useState<EngineStatus>({
    state: 'stopped',
    request_count: 0,
    error_count: 0,
  });
  const [metrics, setMetrics] = useState<SystemMetrics>({
    timestamp: new Date().toISOString(),
    cpu_usage: 0,
    memory_usage: 0,
    disk_usage: 0,
    network_rx: 0,
    network_tx: 0,
  });

  useEffect(() => {
    // Fetch engine status and metrics
    fetchEngineStatus();
    fetchMetrics();

    // Set up polling
    const interval = setInterval(() => {
      fetchEngineStatus();
      fetchMetrics();
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  const fetchEngineStatus = async () => {
    try {
      const response = await fetch('/api/monitoring/engine');
      const data = await response.json();
      setEngineStatus(data);
    } catch (error) {
      console.error('Failed to fetch engine status:', error);
    }
  };

  const fetchMetrics = async () => {
    try {
      const response = await fetch('/api/monitoring/metrics');
      const data = await response.json();
      if (data.length > 0) {
        setMetrics(data[0]);
      }
    } catch (error) {
      console.error('Failed to fetch metrics:', error);
    }
  };

  const controlEngine = async (action: 'start' | 'stop' | 'restart') => {
    try {
      const response = await fetch('/api/engine/control', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ action }),
      });
      const result = await response.json();
      if (result.success) {
        fetchEngineStatus(); // Refresh status
      }
    } catch (error) {
      console.error('Failed to control engine:', error);
    }
  };

  const getStatusBadgeVariant = (state: string) => {
    switch (state) {
      case 'running':
        return 'default';
      case 'stopped':
        return 'secondary';
      case 'starting':
        return 'outline';
      case 'error':
        return 'destructive';
      default:
        return 'secondary';
    }
  };

  const stats = [
    {
      title: 'Engine Status',
      value: (
        <Badge variant={getStatusBadgeVariant(engineStatus.state)}>
          {engineStatus.state.toUpperCase()}
        </Badge>
      ),
      icon: Activity,
      description: 'Current engine state',
    },
    {
      title: 'CPU Usage',
      value: `${metrics.cpu_usage.toFixed(1)}%`,
      icon: Cpu,
      description: 'Current CPU utilization',
    },
    {
      title: 'Memory Usage',
      value: `${(metrics.memory_usage / 1024 / 1024 / 1024).toFixed(1)}GB`,
      icon: HardDrive,
      description: 'System memory usage',
    },
    {
      title: 'Requests',
      value: engineStatus.request_count.toString(),
      icon: Zap,
      description: 'Total requests processed',
    },
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
        <div className="flex space-x-2">
          <Button
            onClick={() => controlEngine('start')}
            disabled={engineStatus.state === 'running'}
            size="sm"
          >
            <Play className="h-4 w-4 mr-2" />
            Start
          </Button>
          <Button
            onClick={() => controlEngine('stop')}
            disabled={engineStatus.state === 'stopped'}
            variant="destructive"
            size="sm"
          >
            <Square className="h-4 w-4 mr-2" />
            Stop
          </Button>
          <Button
            onClick={() => controlEngine('restart')}
            variant="outline"
            size="sm"
          >
            <RotateCcw className="h-4 w-4 mr-2" />
            Restart
          </Button>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => {
          const Icon = stat.icon;
          return (
            <Card key={index}>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">
                  {stat.title}
                </CardTitle>
                <Icon className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{stat.value}</div>
                <p className="text-xs text-muted-foreground">
                  {stat.description}
                </p>
              </CardContent>
            </Card>
          );
        })}
      </div>

      {/* Additional Info */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card>
          <CardHeader>
            <CardTitle>System Information</CardTitle>
          </CardHeader>
          <CardContent>
            <dl className="space-y-2">
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Disk Usage</dt>
                <dd className="text-sm font-medium">{metrics.disk_usage.toFixed(1)}%</dd>
              </div>
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Network RX</dt>
                <dd className="text-sm font-medium">{(metrics.network_rx / 1024 / 1024).toFixed(1)} MB</dd>
              </div>
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Network TX</dt>
                <dd className="text-sm font-medium">{(metrics.network_tx / 1024 / 1024).toFixed(1)} MB</dd>
              </div>
            </dl>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Engine Statistics</CardTitle>
          </CardHeader>
          <CardContent>
            <dl className="space-y-2">
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Total Requests</dt>
                <dd className="text-sm font-medium">{engineStatus.request_count}</dd>
              </div>
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Error Count</dt>
                <dd className="text-sm font-medium text-red-600">{engineStatus.error_count}</dd>
              </div>
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Throughput</dt>
                <dd className="text-sm font-medium">
                  {engineStatus.throughput ? `${engineStatus.throughput.toFixed(1)} req/s` : 'N/A'}
                </dd>
              </div>
              <div className="flex justify-between">
                <dt className="text-sm text-gray-600">Memory Usage</dt>
                <dd className="text-sm font-medium">
                  {engineStatus.memory_usage ? `${(engineStatus.memory_usage / 1024 / 1024 / 1024).toFixed(1)}GB` : 'N/A'}
                </dd>
              </div>
            </dl>
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default Dashboard;
